def tabela_para_xml(tabela: str) -> str:
    linhas = tabela.strip().split("\n")
    cabecalho = linhas[0].split("\t")[1:]  # Pega os n√∫meros das colunas
    xml = []

    for i, linha in enumerate(linhas[1:], start=1):
        valores = linha.split("\t")
        for j, valor in enumerate(valores[1:], start=1):
            # Substitui valores vazios por "0"
            if(valor):
                valor = int(float(valor)*10)
                xml.append(f'<Arc source=\\"{i-1}\\" target=\\"{j-1}\\" weight=\\"{valor}\\"/>')

    return "\n".join(xml)

